function headerMove(){document.addEventListener("scroll",(function(e){window.scrollY>65?document.querySelector(".header").classList.add("fixed"):document.querySelector(".header").classList.remove("fixed")}))}document.addEventListener("DOMContentLoaded",(function(){document.querySelector(".header-menu__icon").addEventListener("click",(function(e){document.querySelector(".header-menu__icon").classList.toggle("active"),document.querySelector(".header-menu").classList.toggle("active"),document.querySelector("body").classList.toggle("lock")})),headerMove()}));